import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { 
  insertDonationSchema, 
  insertProjectSchema, 
  updateProjectSchema,
  projectIdParamSchema,
  projectDonationParamSchema 
} from "@shared/schema";
import { requireAdminAuth, validateUUID } from "./auth-middleware";
import { z } from "zod";

export async function registerRoutes(app: Express): Promise<Server> {
  // System stats endpoint
  app.get("/api/stats", async (req, res) => {
    try {
      const stats = await storage.getSystemStats();
      res.json(stats);
    } catch (error) {
      console.error('Error fetching stats:', error);
      res.status(500).json({ error: "Failed to fetch system statistics" });
    }
  });

  // Donations endpoints
  app.post("/api/donations", requireAdminAuth, async (req, res) => {
    try {
      const validatedData = insertDonationSchema.parse(req.body);

      // Validate project exists if projectId is provided
      if (validatedData.projectId) {
        const projectExists = await storage.getProject(validatedData.projectId);
        if (!projectExists) {
          return res.status(400).json({ 
            error: "Bad Request", 
            message: "Project not found" 
          });
        }
      }

      const donation = await storage.createDonation(validatedData);
      res.status(201).json(donation);
    } catch (error) {
      if (error instanceof z.ZodError) {
        res.status(400).json({ error: "Invalid donation data", details: error.errors });
      } else {
        console.error('Error creating donation:', error);
        res.status(500).json({ error: "Failed to create donation" });
      }
    }
  });

  app.get("/api/donations", async (req, res) => {
    try {
      const donations = await storage.getDonations();
      res.json(donations);
    } catch (error) {
      console.error('Error fetching donations:', error);
      res.status(500).json({ error: "Failed to fetch donations" });
    }
  });

  app.get("/api/donations/project/:projectId", validateUUID("projectId"), async (req, res) => {
    try {
      const { projectId } = req.params;

      // Validate project exists
      const projectExists = await storage.getProject(projectId);
      if (!projectExists) {
        return res.status(404).json({ 
          error: "Not Found", 
          message: "Project not found" 
        });
      }

      const donations = await storage.getDonationsByProject(projectId);
      res.json(donations);
    } catch (error) {
      console.error('Error fetching project donations:', error);
      res.status(500).json({ error: "Failed to fetch project donations" });
    }
  });

  // Projects endpoints
  app.get("/api/projects", async (req, res) => {
    try {
      const projects = await storage.getProjects();
      res.json(projects);
    } catch (error) {
      console.error('Error fetching projects:', error);
      res.status(500).json({ error: "Failed to fetch projects" });
    }
  });

  app.get("/api/projects/featured", async (req, res) => {
    try {
      const projects = await storage.getFeaturedProjects();
      res.json(projects);
    } catch (error) {
      console.error('Error fetching featured projects:', error);
      res.status(500).json({ error: "Failed to fetch featured projects" });
    }
  });

  app.get("/api/projects/:id", validateUUID("id"), async (req, res) => {
    try {
      const { id } = req.params;
      const project = await storage.getProject(id);
      if (!project) {
        return res.status(404).json({ 
          error: "Not Found", 
          message: "Project not found" 
        });
      }
      res.json(project);
    } catch (error) {
      console.error('Error fetching project:', error);
      res.status(500).json({ error: "Failed to fetch project" });
    }
  });

  app.post("/api/projects", requireAdminAuth, async (req, res) => {
    try {
      const validatedData = insertProjectSchema.parse(req.body);
      const project = await storage.createProject(validatedData);
      res.status(201).json(project);
    } catch (error) {
      if (error instanceof z.ZodError) {
        res.status(400).json({ error: "Invalid project data", details: error.errors });
      } else {
        console.error('Error creating project:', error);
        res.status(500).json({ error: "Failed to create project" });
      }
    }
  });

  app.put("/api/projects/:id", requireAdminAuth, validateUUID("id"), async (req, res) => {
    try {
      const { id } = req.params;
      const validatedData = updateProjectSchema.parse(req.body);
      const project = await storage.updateProject(id, validatedData);
      if (!project) {
        return res.status(404).json({ 
          error: "Not Found", 
          message: "Project not found" 
        });
      }
      res.json(project);
    } catch (error) {
      if (error instanceof z.ZodError) {
        res.status(400).json({ error: "Invalid project data", details: error.errors });
      } else {
        console.error('Error updating project:', error);
        res.status(500).json({ error: "Failed to update project" });
      }
    }
  });

  // Health check endpoint
  app.get("/api/health", (req, res) => {
    res.json({ status: "healthy", timestamp: new Date().toISOString() });
  });

  const httpServer = createServer(app);

  return httpServer;
}

server/storage.ts.content:
import { 
  users, 
  donations, 
  projects, 
  systemStats,
  type User, 
  type InsertUser,
  type CreateUser,
  type Donation,
  type InsertDonation,
  type Project,
  type InsertProject,
  type UpdateProject,
  type SystemStats
} from "@shared/schema";
import { db, pool } from "./db";
import { eq, desc, sql } from "drizzle-orm";
import { hashPassword, verifyPassword } from "./auth-utils";

export interface IStorage {
  // User methods
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: CreateUser): Promise<User>;
  verifyUserPassword(username: string, password: string): Promise<User | null>;

  // Donation methods
  createDonation(donation: InsertDonation): Promise<Donation>;
  getDonations(): Promise<Donation[]>;
  getDonationsByProject(projectId: string): Promise<Donation[]>;

  // Project methods
  getProjects(): Promise<Project[]>;
  getProject(id: string): Promise<Project | undefined>;
  createProject(project: InsertProject): Promise<Project>;
  updateProject(id: string, updates: UpdateProject): Promise<Project | undefined>;
  getFeaturedProjects(): Promise<Project[]>;

  // Stats methods
  getSystemStats(): Promise<SystemStats>;
  updateSystemStats(): Promise<SystemStats>;
}

export class DatabaseStorage implements IStorage {
  // User methods
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.username, username));
    return user || undefined;
  }

  async createUser(createUser: CreateUser): Promise<User> {
    const passwordHash = await hashPassword(createUser.password);
    const [user] = await db
      .insert(users)
      .values({
        username: createUser.username,
        passwordHash
      })
      .returning();
    return user;
  }

  async verifyUserPassword(username: string, password: string): Promise<User | null> {
    const user = await this.getUserByUsername(username);
    if (!user) {
      return null;
    }

    const isValid = await verifyPassword(password, user.passwordHash);
    return isValid ? user : null;
  }

  // Donation methods
  async createDonation(donation: InsertDonation): Promise<Donation> {
    // Use transaction to ensure data consistency
    return await db.transaction(async (tx) => {
      // Create the donation
      const [newDonation] = await tx
        .insert(donations)
        .values(donation)
        .returning();

      // Update project current amount if projectId is provided
      if (donation.projectId) {
        await tx
          .update(projects)
          .set({
            currentAmount: sql`${projects.currentAmount} + ${donation.amount}`,
            updatedAt: new Date()
          })
          .where(eq(projects.id, donation.projectId));
      }

      // Calculate and update system stats within the transaction
      const [totals] = await tx
        .select({
          totalRaised: sql<string>`COALESCE(SUM(${donations.amount}), 0)`,
          totalDonors: sql<number>`COUNT(DISTINCT CASE WHEN ${donations.donorEmail} IS NOT NULL THEN ${donations.donorEmail} END)`,
          totalProjects: sql<number>`(SELECT COUNT(*) FROM ${projects})`
        })
        .from(donations);

      // Update or insert stats within transaction
      const [existingStats] = await tx.select().from(systemStats).limit(1);

      if (existingStats) {
        await tx
          .update(systemStats)
          .set({
            totalRaised: totals.totalRaised,
            totalDonors: totals.totalDonors,
            totalProjects: totals.totalProjects,
            updatedAt: new Date()
          })
          .where(eq(systemStats.id, existingStats.id));
      } else {
        await tx
          .insert(systemStats)
          .values({
            totalRaised: totals.totalRaised,
            totalDonors: totals.totalDonors,
            totalProjects: totals.totalProjects
          });
      }

      return newDonation;
    });
  }

  async getDonations(): Promise<Donation[]> {
    return await db.select().from(donations).orderBy(desc(donations.createdAt));
  }

  async getDonationsByProject(projectId: string): Promise<Donation[]> {
    return await db
      .select()
      .from(donations)
      .where(eq(donations.projectId, projectId))
      .orderBy(desc(donations.createdAt));
  }

  // Project methods
  async getProjects(): Promise<Project[]> {
    return await db.select().from(projects).orderBy(desc(projects.createdAt));
  }

  async getProject(id: string): Promise<Project | undefined> {
    const [project] = await db.select().from(projects).where(eq(projects.id, id));
    return project || undefined;
  }

  async createProject(project: InsertProject): Promise<Project> {
    const [newProject] = await db
      .insert(projects)
      .values(project)
      .returning();

    await this.updateSystemStats();
    return newProject;
  }

  async updateProject(id: string, updates: UpdateProject): Promise<Project | undefined> {
    const [updatedProject] = await db
      .update(projects)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(projects.id, id))
      .returning();

    return updatedProject || undefined;
  }

  async getFeaturedProjects(): Promise<Project[]> {
    return await db
      .select()
      .from(projects)
      .where(eq(projects.isFeatured, true))
      .orderBy(desc(projects.updatedAt));
  }

  // Stats methods
  async getSystemStats(): Promise<SystemStats> {
    let [stats] = await db.select().from(systemStats).limit(1);

    if (!stats) {
      // Initialize stats if they don't exist
      [stats] = await db
        .insert(systemStats)
        .values({})
        .returning();
    }

    return stats;
  }

  async updateSystemStats(): Promise<SystemStats> {
    // Calculate totals from actual data
    const [totals] = await db
      .select({
        totalRaised: sql<string>`COALESCE(SUM(${donations.amount}), 0)`,
        totalDonors: sql<number>`COUNT(DISTINCT CASE WHEN ${donations.donorEmail} IS NOT NULL THEN ${donations.donorEmail} END)`,
        totalProjects: sql<number>`(SELECT COUNT(*) FROM ${projects})`
      })
      .from(donations);

    // Update or insert stats
    const [stats] = await db.select().from(systemStats).limit(1);

    if (stats) {
      const [updatedStats] = await db
        .update(systemStats)
        .set({
          totalRaised: totals.totalRaised,
          totalDonors: totals.totalDonors,
          totalProjects: totals.totalProjects,
          updatedAt: new Date()
        })
        .where(eq(systemStats.id, stats.id))
        .returning();
      return updatedStats;
    } else {
      const [newStats] = await db
        .insert(systemStats)
        .values({
          totalRaised: totals.totalRaised,
          totalDonors: totals.totalDonors,
          totalProjects: totals.totalProjects
        })
        .returning();
      return newStats;
    }
  }
}

export const storage = new DatabaseStorage();

