  --shadow-2xs: 0px 2px 0px 0px hsl(220 13% 16% / 0.00);
  --shadow-xs: 0px 2px 0px 0px hsl(220 13% 16% / 0.00);
  --shadow-sm: 0px 2px 0px 0px hsl(220 13% 16% / 0.00), 0px 1px 2px -1px hsl(220 13% 16% / 0.00);
  --shadow: 0px 2px 0px 0px hsl(220 13% 16% / 0.00), 0px 1px 2px -1px hsl(220 13% 16% / 0.00);
  --shadow-md: 0px 2px 0px 0px hsl(220 13% 16% / 0.00), 0px 2px 4px -1px hsl(220 13% 16% / 0.00);
  --shadow-lg: 0px 2px 0px 0px hsl(220 13% 16% / 0.00), 0px 4px 6px -1px hsl(220 13% 16% / 0.00);
